import React from "react";
import { useState } from "react";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import styles from "./LoanApply.module.css";
import InputGroup from "react-bootstrap/InputGroup";
import Row from "react-bootstrap/Row";
import { Routes, Route, useNavigate } from "react-router-dom";
import axios from "axios";
import CustomButton from "./customButton";
import background from "./../assets/login-bg.jpg";

const LoanApply = () => {
  const [validated, setValidated] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    } else {
      const data = {
        empId: EmpId,
        empName: ICategory,
        IDes: IDes,
        IMake: IMake,
        Ivalue: IValue,
      };
      const url = "https://localhost:7033/api/ApplyLoans";
      axios
        .post(url, data)
        .then((result) => {
          alert("succesfully applied for loan");
          navigate("/DisplayLoanCards");
        })
        .catch((error) => {
          alert(error);
        });
    }
    setValidated(true);
  };
  const navigate = useNavigate();
  const [EmpId, setId] = useState("");
  const [ICategory, setCategory] = useState(" ");
  const [IDes, setDes] = useState(" ");
  const [IMake, setMake] = useState(" ");
  const [IValue, setIvalue] = useState(0);
  const handleIdChange = (value) => {
    setId(value);
  };
  const handleCategoryChange = (value) => {
    setCategory(value);
    setMake("");
    setIvalue(0);
  };
  const handleDesChange = (value) => {
    setDes(value);
  };
  const handleItemMakeChange = (value) => {
    setMake(value);
    if(ICategory === 'furniture'){
      switch (value){
        case 'wodden':
          setIvalue(5000);
          break;
        case 'plastic':
          setIvalue(2000);
          break;
        case 'glass':
          setIvalue(1000);
          break;
        default:
          setIvalue(0);
      }
    }else if(ICategory === 'crockery'){
      switch (value){
        case 'wodden':
          setIvalue(5000);
          break;
        case 'plastic':
          setIvalue(2000);
          break;
        case 'glass':
          setIvalue(1000);
          break;
        default:
          setIvalue(0);
      }
    }else if(ICategory === 'stationary'){
      switch (value){
        case 'wooden':
          setIvalue(5000);
          break;
        case 'plastic':
          setIvalue(2000);
          break;
        case 'glass':
          setIvalue(1000);
          break;
        default:
          setIvalue(0);
      }
    }else{
      setIvalue(0);
    }
  };
  // const handleValueChange = (value) => {
  //   setIvalue(value);
  // };
  return (
    <div
      className={styles["Loan"]}
      style={{ backgroundImage: `url(${background})` }}
    >
      <Form noValidate validated={validated} onSubmit={handleSubmit}>
        <Row className="mb-3">
          <Form.Group as={Col} md="6" controlId="validationCustom01">
            <Form.Label>Employee Id</Form.Label>
            <Form.Control
              required
              type="text"
              placeholder="Please enter your Employee id"
              defaultValue=""
              onChange={(e) => handleIdChange(e.target.value)}
            />
            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
          </Form.Group>
          <Form.Group as={Col} md="6" controlId="validationCustom02">
            <Form.Label>Item Category</Form.Label>
            <Form.Select
              aria-label="Default select example"
              required
              onChange={(e) => handleCategoryChange(e.target.value)}
            >
              <option value="">choose a category</option>
              <option value="furniture">Furniture</option>
              <option value="stationary">Stationary</option>
              <option value="crockery">Crockery</option>
            </Form.Select>
            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
          </Form.Group>
        </Row>
        <Row className="mb-3">
          <Form.Group as={Col} md="6" controlId="validationCustom03">
            <Form.Label>Item Description</Form.Label>
            <Form.Control
              type="text"
              placeholder="Description of item"
              required
              onChange={(e) => handleDesChange(e.target.value)}
            />
            <Form.Control.Feedback type="invalid">
              Please provide a valid Description.
            </Form.Control.Feedback>
          </Form.Group>
          {ICategory && ( <Form.Group as={Col} md="6" controlId="validationCustom02">
            <Form.Label>Item Make</Form.Label>
            <Form.Select
              aria-label="Default select example"
              required
              value={IMake}
              onChange={(e) => handleItemMakeChange(e.target.value)}
            >
              <option value="">choose a material</option>
              <option value="wodden">Wodden</option>
              <option value="glass">Glass</option>
              <option value="plastic">Plastic</option>
            </Form.Select>
            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
          </Form.Group>
         )}
         
        </Row>
        <Row className="mb-3">
        {/* <Form.Group as={Col} md="6" controlId="validationCustom04">
            <Form.Label>Item value</Form.Label>
            {IValue}
            <Form.Control.Feedback type="invalid">
              Please provide a valid number.
            </Form.Control.Feedback>
          </Form.Group> */}
          <label>Item value</label>
          {IValue}
        </Row>
        <div>
          <CustomButton
            type="submit"
            style={{ marginLeft: "40%", marginTop: "2%" }}
          >
            Apply
          </CustomButton>
        </div>
      </Form>
    </div>
  );
};

export default LoanApply;
