import React from "react";
import { Button } from "react-bootstrap";

const ItemTableRow = ({ item, editItem, deleteItem }) => {
  return (
    <tr>
      <td>{item.itemid}</td>
      <td>{item.itemDescription}</td>
      <td>{item.issueStatus}</td>
      <td>{item.itemMake}</td>
      <td>{item.itemCategory}</td>
      <td>{item.itemValue}</td>
      <td>
        <Button
          variant="secondary"
          onClick={() =>
            editItem(item.item_id, {
              Itemid: item.item_id,
              ItemDescription: item.item_description,
              IssueStatus: item.item_status,
              ItemMake: item.item_make,
              ItemCategory: item.item_category,
              ItemValue: item.item_valuation,
            })
          }
        >
          Edit
        </Button>
        <Button variant="danger" onClick={() => deleteItem(item.item_id)}>
          Delete
        </Button>
      </td>
    </tr>
  );
};

export default ItemTableRow;
