import React, {useState} from "react";
import { Button } from "react-bootstrap";

const ItemTableRow = ({ item, editItem, deleteItem }) => {

  const [showModal, setShowModal] = useState(false);


  const openModal = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };
  return (
    <tr>
      <td>{item.itemid}</td>
      <td>{item.itemDescription}</td>
      <td>{item.issueStatus}</td>
      <td>{item.itemMake}</td>
      <td>{item.itemCategory}</td>
      <td>{item.itemValue}</td>
      <td>
      <ItemFormModal show={showModal} onHide={closeModal} addItem={(input)=>editItem(item.itemid, input)} a={item.item_id} b={item.description} c={item.item_status} d={item.item_make} g={item.item_category} f={item.item_valuation}/>
        <Button
          variant="secondary"
          onClick={openModal}
        >
          Edit
        </Button>
        <Button variant="danger" onClick={() => deleteItem(item.itemid)}>
          Delete
        </Button>
      </td>
    </tr>
  );
};

export default ItemTableRow;
